import React, {useRef, useState } from 'react'
// import './CRUD.css';

function CRUD() {
    const list = [
        {
            id:1,
            name:"HP",
            price:"7890"
        },
         {
            id:2,
            name:"lenevo",
            price:"7890"
        },

    ]
    const [lists, setList] = useState(list)
    const [updateState, setUpdateState]=useState(-1)
  return (
    <>
    <div className='crud'>
        <div>
        <Addlist setList={setList}/>
        <form onSubmit={handleSubmit}>
        <table>
            {
                lists.map((current)=>(
                    updateState === id? <EditList current={current} lists={lists} setList={setList} />:
                <tr>
                  <td>{current.name} </td>
                  <td> {current.price}  </td>
                  <td>
                    <button className='delete' type='button' onClick={()=> handleDelete(current.id)}  >Delete</button>
                    <button className='edit' onClick={()=>handleEdit(current.id)} >Edit</button>
                  </td>
                </tr>
                 ) )
            }
        </table>
        </form>
        </div>
    </div>
    </>
  )
  function handleEdit(id){
    setUpdateState(id)
}
function handleDelete(id){
    const newlist = lists.filter((li)=>li.id !== id )
    setList(newlist)
}
function handleSubmit(event){
    event.preventDefault()
    const name=event.target.elements.name.value
    const price=event.target.elements.price.value
    const newlist= lists.map((li)=>(
        li.add=== updateState ? {...li, name:name, price:price}  :li
        
    ))
    setLists(newlist) 
    setUpdateState(-1)
}
}
function EditList({current, lists, setList}){
    function handInputname(event){
        const name=event.target.name;
        const value=event.target.value;
       const newlist= lists.map((li)=>(
         li.id=== current.id ? {...li, name:value}  :li  
        ))
     setList(newlist) 
    }
    function handInputprice(event){
        const name=event.target.name;
        const value=event.target.value;
       const newlist= lists.map((li)=>(
         li.add=== current.id ? {...li, price:value}  :li  
        ))
     setList(newlist) 
    }
    return(
        <tr>
            <td><input type="text" onChange={handInputname} name="name" value={current.name} /></td>
            <td><input type="text" onChange={handInputprice} name="price" value={current.price} /></td>
            <td><button type="submit">Update</button></td>

        </tr>
    )
}

function Addlist({setList}){
    const nameRef=useRef()
    const priceRef=useRef()
    function handleSubmit(event){
        event.preventDefault();
        const name = event.target.elements.name.value;
        const price = event.target.elements.price.value;
        const newlist ={
            id:3,
            name,
            price
        }
   setList ((prevList)=>{
    return prevList.concat(newlist)
   })
   nameRef.current.value= ""
   priceRef.current.value= ""


    }
    return(
        <form className='addForm' onSubmit={handleSubmit}>
            <input type="text" name="name" placeholder="Enter the Name" ref={nameRef}/>
            <input type="text" name="price" placeholder="Enter the price" ref={priceRef}/>
            <button type="submit">Add</button>

        </form>
    )
}
export default CRUD
